import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [books, setBooks] = useState([]);
  const [book, setBook] = useState({ title: '', author: '', year: '' });

  const fetchBooks = async () => {
    const res = await axios.get('http://52.91.45.31:5000/books');
    setBooks(res.data);
  };

  const addBook = async () => {
    await axios.post('http://52.91.45.31:5000/books', book);
    fetchBooks();
  };

  const deleteBook = async (id) => {
    await axios.delete(`http://52.91.45.31:5000/books/${id}`);
    fetchBooks();
  };

  useEffect(() => { fetchBooks(); }, []);

  return (
    <div>
      <h2>Book List</h2>
      <input placeholder="Title" onChange={e => setBook({ ...book, title: e.target.value })} />
      <input placeholder="Author" onChange={e => setBook({ ...book, author: e.target.value })} />
      <input placeholder="Year" onChange={e => setBook({ ...book, year: e.target.value })} />
      <button onClick={addBook}>Add</button>
      <ul>
        {books.map(b => (
          <li key={b._id}>{b.title} <button onClick={() => deleteBook(b._id)}>X</button></li>
        ))}
      </ul>
    </div>
  );
}

export default App;
