import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [books, setBooks] = useState([]);
  const [book, setBook] = useState({ title: '', author: '', price: '' });
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  const fetchBooks = async () => {
    try {
      const res = await axios.get('http://52.91.45.31:5000/books');
      setBooks(res.data);
    } catch (error) {
      setErrorMessage('Error fetching books.');
    }
  };

  const addBook = async () => {
    if (!book.title || !book.author || !book.price) {
      setErrorMessage('All fields must be filled out.');
      return;
    }

    try {
      await axios.post('http://52.91.45.31:5000/books', {
        ...book,
        price: parseFloat(book.price),
      });

      fetchBooks();
      setSuccessMessage('Book added successfully!');
      setErrorMessage('');
      setBook({ title: '', author: '', price: '' });
    } catch (error) {
      console.error(error);
      setErrorMessage('Error adding book.');
      setSuccessMessage('');
    }
  };

  const deleteBook = async (id) => {
    try {
      await axios.delete(`http://52.91.45.31:5000/books/${id}`);
      fetchBooks();
      setSuccessMessage('Book deleted successfully!');
      setErrorMessage('');
    } catch (error) {
      setErrorMessage('Error deleting book.');
      setSuccessMessage('');
    }
  };

  useEffect(() => {
    fetchBooks();
  }, []);

  return (
    <div style={{ padding: '20px' }}>
      <h2>üìö Book List</h2>

      {successMessage && <p style={{ color: 'green' }}>{successMessage}</p>}
      {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}

      <input
        placeholder="Title"
        value={book.title}
        onChange={(e) => setBook({ ...book, title: e.target.value })}
      />
      <input
        placeholder="Author"
        value={book.author}
        onChange={(e) => setBook({ ...book, author: e.target.value })}
      />
      <input
        placeholder="Price"
        type="number"
        value={book.price}
        onChange={(e) => setBook({ ...book, price: e.target.value })}
      />
      <button onClick={addBook}>Add</button>

      <ul>
        {books.map((b) => (
          <li key={b._id}>
            <strong>{b.title}</strong> by {b.author} - üí∞${b.price}
            <button onClick={() => deleteBook(b._id)} style={{ marginLeft: '10px' }}>
              ‚ùå
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
